import { ThemeIcon, Text, Title, Container, SimpleGrid, createStyles, rem } from '@mantine/core';

 const MOCKDATA = [
  {

    description:
      'Very helpful! The app works great compared  to others on the App Store, plus the customer service helped me with a few things and followed up. ',
  },
  {

    description:
      'Very helpful! The app works great compared  to others on the App Store, plus the customer service helped me with a few things and followed up. ',
  },{

    description:
      'Very helpful! The app works great compared  to others on the App Store, plus the customer service helped me with a few things and followed up. ',
  },
];


 function Feature({  description }) {
  return (
    <div>
      <Text size="xl" mb={7}>
        {title}
      </Text>
      <Text size="lg" color="dimmed" sx={{ lineHeight: 1.6 }}>
        {description}
      </Text>
    </div>
  );
}

const useStyles = createStyles((theme) => ({


  title: {
    fontFamily: `Greycliff CF, ${theme.fontFamily}`,
    fontWeight: 900,
    marginBottom: theme.spacing.md,
    textAlign: 'center',

    [theme.fn.smallerThan('sm')]: {
      fontSize: rem(28),
      textAlign: 'center',
    },
  },

  description: {
    textAlign: 'center',

    [theme.fn.smallerThan('sm')]: {
      textAlign: 'left',
    },
  },
}));



 function FeaturesGrid({ title, description, data = MOCKDATA }) {
  const { classes } = useStyles();
  const features = data.map((feature, index) => <Feature {...feature} key={index} />);

  return (
    <Container size={1200}>
      <Title className={classes.title}>{title}</Title>

      <Container size={560} p={0}>
        <Text size="lg" className={classes.description}>
          {description}
        </Text>
      </Container>

      <SimpleGrid
        mt={50}
        cols={2}
        spacing={100}
        breakpoints={[
          { maxWidth: 980, cols: 2, spacing: 'xl' },
          { maxWidth: 755, cols: 1, spacing: 'xl' },
        ]}
      >
        {features}
      </SimpleGrid>
    </Container>
  );
}

export default FeaturesGrid
